<?php echo "";include 'dbconnect.php';try{    $bdd = new PDO('mysql:host='.$hostBdd.';dbname='.$baseBdd.';charset=utf8', "$userBdd", "$passBdd", array(PDO::ATTR_ERRMODE => PDO::ERRMODE_EXCEPTION));} catch(Exception $e){    die($e);}if($_POST['type'] == 'connAdmin'){    $utilisateur = $_POST['utilisateur'];    $passe = $_POST['passe'];    session_start();    $_SESSION['utilisateur'] = $utilisateur;    $requete = $bdd->prepare('SELECT identifiant, pass FROM admin ', array(PDO::ATTR_CURSOR => PDO::CURSOR_FWDONLY));    $requete->execute();    while($resultat = $requete->fetch()){        if($utilisateur == $resultat['identifiant']){            if($passe == $resultat['pass']){                echo 'saisie réussite';                $_SESSION['utilisateur'] = $utilisateur;            } else {                echo 'mot de passe incorrect';            }        } else {            echo 'identifiant incorrect';        }    }} else if($_POST['type'] == 'ajoutCategorie'){    $libelle = $_POST['libelle'];    $requete = $bdd->prepare('INSERT INTO categorie (libelle) VALUES (:libelle)');    $requete->bindParam(':libelle', $libelle, PDO::PARAM_STR);    $requete->execute();    echo 'Catégorie ajouté !';}